package com.example.demo.solver;

import com.fasterxml.jackson.core.type.TypeReference;
import com.fasterxml.jackson.databind.ObjectMapper;
import org.w3c.dom.Document;
import org.w3c.dom.Element;
import org.w3c.dom.NodeList;
import org.xml.sax.InputSource;

import javax.xml.parsers.DocumentBuilder;
import javax.xml.parsers.DocumentBuilderFactory;
import java.io.StringReader;
import java.util.*;

public class CaptchaSolver {
    private static final String DEFAULT_MODEL = ""; // Your base64 model string here

    /**
     * Solve SVG captcha using a predefined model.
     *
     * @param svgCaptcha The SVG captcha as XML string
     * @param model      The predefined model (base64 string), can be null to use default model
     * @param length     Amount of characters in the captcha
     * @return Solved captcha value
     */
    public static String solveCaptcha(String svgCaptcha, String model, int length) {
        try {
            // Parse the model
            String modelToUse = (model != null) ? model : DEFAULT_MODEL;
            String decodedModel = new String(Base64.getDecoder().decode(modelToUse));

            // Use Jackson to parse JSON
            ObjectMapper mapper = new ObjectMapper();
            Map<String, String> parsedModel = mapper.readValue(decodedModel,
                    new TypeReference<Map<String, String>>() {});

            // Parse SVG
            DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
            // Enable secure processing
            factory.setFeature("http://xml.org/sax/features/external-general-entities", false);
            factory.setFeature("http://xml.org/sax/features/external-parameter-entities", false);
            factory.setExpandEntityReferences(false);

            DocumentBuilder builder = factory.newDocumentBuilder();
            Document document = builder.parse(new InputSource(new StringReader(svgCaptcha)));

            // Get all path elements
            NodeList paths = document.getElementsByTagName("path");
            List<Element> noStrokePaths = new ArrayList<>();

            // Filter paths without stroke
            for (int i = 0; i < paths.getLength(); i++) {
                Element path = (Element) paths.item(i);
                if (path.getAttribute("stroke").isEmpty()) {
                    noStrokePaths.add(path);
                }
            }

            // Extract and sort values
            List<Integer> vals = new ArrayList<>();
            for (Element path : noStrokePaths) {
                String d = path.getAttribute("d");
                int val = Integer.parseInt(d.split("\\.")[0].replace("M", ""));
                vals.add(val);
            }

            List<Integer> sortedVals = new ArrayList<>(vals);
            Collections.sort(sortedVals);

            // Create solution array
            String[] solution = new String[length];
            Arrays.fill(solution, "");

            // Solve captcha
            for (int i = 0; i < noStrokePaths.size(); i++) {
                String pattern = noStrokePaths.get(i).getAttribute("d")
                        .replaceAll("[\\d.\\s]*", "");
                int index = sortedVals.indexOf(vals.get(i));
                solution[index] = parsedModel.get(pattern);
            }

            // Join solution
            return String.join("", solution);

        } catch (Exception e) {
            throw new RuntimeException("Failed to solve captcha", e);
        }
    }

    /**
     * Overloaded method with default length of 6
     */
    public static String solveCaptcha(String svgCaptcha, String model) {
        return solveCaptcha(svgCaptcha, model, 6);
    }

    /**
     * Overloaded method with default model and length
     */
    public static String solveCaptcha(String svgCaptcha) {
        return solveCaptcha(svgCaptcha, null, 6);
    }
}